
#ifndef GSM_AMR_TYPEDEFS_H
#define GSM_AMR_TYPEDEFS_H


#include    "oscl_base.h"





#ifndef Word8
typedef int8        Word8;
#endif

#ifndef UWord8
typedef uint8       UWord8;
#endif



#ifndef Word16
typedef int16       Word16;

#endif

#ifndef UWord16
typedef uint16      UWord16;

#endif


#ifndef Word32
typedef int32       Word32;
#endif

#ifndef UWord32
typedef uint32      UWord32;
#endif


#ifndef Bool
typedef int     Bool;
#endif

#ifndef FALSE
#define FALSE       0
#endif

#ifndef TRUE
#define TRUE        1
#endif

#ifndef OFF
#define OFF     0
#endif

#ifndef ON
#define ON      1
#endif

#ifndef NO
#define NO      0
#endif

#ifndef YES
#define YES     1
#endif

#ifndef SUCCESS
#define SUCCESS     0
#endif

#ifndef  NULL
#define  NULL       0
#endif

#ifndef Flag
typedef int32     Flag;
#endif

#endif   /*  GSM_AMR_TYPEDEFS_H */
